<script setup lang="ts">
import { ref, onMounted } from 'vue';
import { gsap } from 'gsap';

const path1 = ref<SVGPathElement | null>(null);
const path2 = ref<SVGPathElement | null>(null);
const path3 = ref<SVGPathElement | null>(null);

const animatePaths = () => {
  const paths = [path1.value, path2.value, path3.value];
  const tl = gsap.timeline();

  paths.forEach(path => {
    if (path) {
      const length = path.getTotalLength();
      tl.set(path, {
        strokeDasharray: length,
        strokeDashoffset: length,
        stroke: '#BC0000',
        fill: 'none'
      });
      tl.to(path, {
        strokeDashoffset: 0,
        duration: 1,
        ease: 'power1.inOut'
      }, "-=.5"); // Overlap animations slightly
    }
  });

  // After drawing, fill the paths
  tl.to(paths, {
    fill: '#BC0000',
    duration: 0.5
  });
};

onMounted(() => {
  animatePaths();
});

defineExpose({
  animatePaths
})
</script>

